$(function(){
	$('.header .menu .mobile-menu').click(function(){
		if($(this).hasClass('active')) {
			$(this).removeClass('active');
			$(this).siblings('ul').fadeOut(100);
			$('.header .menu').removeClass('mobile');
		} else {
			$(this).addClass('active');
			$(this).siblings('ul').fadeIn(100);
			$('.header .menu').addClass('mobile');
		}
	});
});

$(window).bind("load", AdoptMenu);
$(window).bind("resize", AdoptMenu);
$(window).bind("orientationchange", AdoptMenu);

function AdoptMenu() {
	var refSize = $(window).width();

	if(refSize + 17 >= 1010) {
		$('.header .menu ul').show();
		$('.header .menu .mobile-menu').removeClass('active');
	} else {
		$('.header .menu ul').hide();
		$('.header .menu .mobile-menu').removeClass('active');
	}
}
